const axios = require("axios");

const sendSms = {
  send: function (api_key, username, sender_id, message, phone) {
    let sms = {
      api_key: api_key,
      username: username,
      sender_id: sender_id,
      message: message,
      phone: phone,
    };
    const options = {
      method: "post",
      url: "http://bulksms.mobitechtechnologies.com/api/sendsms",
      data: sms,
      transformResponse: [
        (data) => {
          // transform the response

          return data;
        },
      ],
    };

    // send the request
    axios(options);
  },
};

module.exports = sendSms;
